// var log = require('logger')(module);

// Логгер добавляет адрес модуля (который его вызвал) к тому, что нужно получить
// Т.е. к любому запросу добавится строка с именем модуля

module.exports = function(module) {
    return function( /*...*/ ) {
        //превращаем аргументы в массив и добавляем к имени файла
        // или наоборот
        var args = [].slice.call(arguments).concat( [module.filename] );

        //передаём в консоль аргументы и имя файла
        console.log.apply(console, args);
    }
}